
// Base64 encoded Vazir font data
// This is a minimal subset of Vazir font for Persian characters
export const VAZIR_FONT_BASE64 = `
data:font/truetype;charset=utf-8;base64,`;

// Extract just the base64 part without the data URL prefix
export const getVazirFontData = (): string => {
  return VAZIR_FONT_BASE64.split(',')[1] || '';
};
